<?phpclass WebUser extends CWebUser {     public function __get($name)     {         if ($this->hasState('__userInfo')) {             $user=$this->getState('__userInfo',array());             if (isset($user[$name])) {                 return $user[$name];             } else {                return null;            }        }                 return parent::__get($name);     }     public function login($identity, $duration) {         // Yii::app()->session->setTimeout(3);        $this->setState('__userInfo', $identity->getUser());         // F::dump(Yii::app()->session->getTimeout());        // F::dump(Yii::app()->session->getTimeout());        parent::login($identity, $duration);         // F::dump(Yii::app()->session->getTimeout());        // exit();    } } 